import"./hoisted.DvHeWgiy.js";document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".accordion .label").forEach(n=>{n.addEventListener("click",()=>{const e=n.parentElement;if(!e)return;const t=e.querySelector(".content");t instanceof HTMLElement&&(e.classList.contains("active")?t.style.maxHeight="0":t.style.maxHeight=`${t.scrollHeight}px`,e.classList.toggle("active"))})})});
